
public class Ej1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=20;
		int b=10;
		
		int suma=a+b;
		System.out.println(suma);
		
		int resta=a-b;
		System.out.println(resta);
		
		int multiplica=a*b;
		System.out.println(multiplica);
		
		int division=a/b;
		System.out.println(division);
		
		int resto=a%b;
		System.out.println(resto);
	}

}
