
public class Ej4App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int N=40;
		
		System.out.println("Valor inicial de N = " +N);
		
		int incremento= N+77;
		
		System.out.println("El resultado de incrementar N en 77 = " +incremento);
		
		int decremento = incremento-3;
		
		System.out.println("El resultado de decrementar en 3 = " +decremento);
		
		int duplicar = decremento*2;
		
		System.out.println("El resultado de duplicar el valor de N = " +duplicar);
		
	}

}
